import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse, HttpErrorResponse } from '@angular/common/http';
import { LazyLoadEvent } from 'primeng/primeng';
import { MessageService } from '../../service/message.service';
import { PageResponse, PageRequestByExample } from '../../support/paging';
import { Version } from './version';
import { Observable } from 'rxjs/Rx';
import { catchError, map } from 'rxjs/operators';
import 'rxjs/add/observable/throw';

@Injectable()
export class VersionService {

    constructor(private http: HttpClient, private messageService : MessageService) {}

    /**
     * Get a Version by id.
     */
    getVersion(id : any) : Observable<Version> {
        return this.http.get('/api/versions/' + id)
            .pipe(
                map(response => new Version(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Update the passed version.
     */
    update(version : Version) : Observable<Version> {
        let body = version;

        return this.http.put('/api/versions/', body)
            .pipe(
                map(response => new Version(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Load a page (for paginated datatable) of Version using the passed
     * version as an example for the search by example facility.
     */
    getPage(version : Version, event : LazyLoadEvent) : Observable<PageResponse<Version>> {
        let req = new PageRequestByExample(version, event);
        let body = req;

        return this.http.post<PageResponse<any>>('/api/versions/page', body)
            .pipe(
                map(pr =>  new PageResponse<Version>(pr.totalPages, pr.totalElements, Version.toArray(pr.content))),
                catchError(this.handleError)
            );
    }

    /**
     * Performs a search by example on 1 attribute (defined on server side) and returns at most 10 results.
     * Used by VersionCompleteComponent.
     */
    complete(query : string) : Observable<Version[]> {
        let body = {'query': query, 'maxResults': 10};
        return this.http.post<any[]>('/api/versions/complete', body)
            .pipe(
                map(response => Version.toArray(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Delete an Version by id.
     */
    delete(id : any) {
        return this.http.delete('/api/versions/' + id)
            .pipe(catchError(this.handleError));
    }

    // sample method from angular doc
    private handleError (error: HttpErrorResponse) {
        // TODO: seems we cannot use messageService from here...
        let errMsg = (error.message) ? error.message : 'Server error';
        console.error(errMsg);
        if (error.status === 401 ) {
            window.location.href = '/';
        }
        return Observable.throw(errMsg);
    }
}
