import { Injectable } from '@angular/core';
import { HttpClient, HttpResponse, HttpErrorResponse } from '@angular/common/http';
import { LazyLoadEvent } from 'primeng/primeng';
import { MessageService } from '../../service/message.service';
import { PageResponse, PageRequestByExample } from '../../support/paging';
import { WeekNumber } from './weekNumber';
import { Observable } from 'rxjs/Rx';
import { catchError, map } from 'rxjs/operators';
import 'rxjs/add/observable/throw';

@Injectable()
export class WeekNumberService {

    constructor(private http: HttpClient, private messageService : MessageService) {}

    /**
     * Get a WeekNumber by id.
     */
    getWeekNumber(id : any) : Observable<WeekNumber> {
        return this.http.get('/api/weekNumbers/' + id)
            .pipe(
                map(response => new WeekNumber(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Update the passed weekNumber.
     */
    update(weekNumber : WeekNumber) : Observable<WeekNumber> {
        let body = weekNumber;

        return this.http.put('/api/weekNumbers/', body)
            .pipe(
                map(response => new WeekNumber(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Load a page (for paginated datatable) of WeekNumber using the passed
     * weekNumber as an example for the search by example facility.
     */
    getPage(weekNumber : WeekNumber, event : LazyLoadEvent) : Observable<PageResponse<WeekNumber>> {
        let req = new PageRequestByExample(weekNumber, event);
        let body = req;

        return this.http.post<PageResponse<any>>('/api/weekNumbers/page', body)
            .pipe(
                map(pr =>  new PageResponse<WeekNumber>(pr.totalPages, pr.totalElements, WeekNumber.toArray(pr.content))),
                catchError(this.handleError)
            );
    }

    /**
     * Performs a search by example on 1 attribute (defined on server side) and returns at most 10 results.
     * Used by WeekNumberCompleteComponent.
     */
    complete(query : string) : Observable<WeekNumber[]> {
        let body = {'query': query, 'maxResults': 10};
        return this.http.post<any[]>('/api/weekNumbers/complete', body)
            .pipe(
                map(response => WeekNumber.toArray(response)),
                catchError(this.handleError)
            );
    }

    /**
     * Delete an WeekNumber by id.
     */
    delete(id : any) {
        return this.http.delete('/api/weekNumbers/' + id)
            .pipe(catchError(this.handleError));
    }

    // sample method from angular doc
    private handleError (error: HttpErrorResponse) {
        // TODO: seems we cannot use messageService from here...
        let errMsg = (error.message) ? error.message : 'Server error';
        console.error(errMsg);
        if (error.status === 401 ) {
            window.location.href = '/';
        }
        return Observable.throw(errMsg);
    }
}
